class Yui extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
        };
    }

    render(){
        const [show, setShow] = React.useState()
        return(<Box
            background = "white"
        >
            <Box>
                <Button label="Juice" onClick={() => setShow(true)} />

                <Layer
                    onEsc={() => setShow(false)}
                    onClickOutside={() => setShow(false)}
                    >
                    <Button label="close" onClick={() => setShow(false)} />
                </Layer>

            </Box>
            <Box>
                <Button label="photo" onClick={() => setShow(true)} />   
            </Box>
            <Box>
                <Button label="video" onClick={() => setShow(true)} />
            </Box>

            <h1>Hi I am Yuichiro</h1>
        </Box>)
    }
}

export default Yui;
