import React from 'react';
import { Box, Button, Layer } from 'grommet';
import { CaretUp, CaretDown, CaretNext, CaretPrevious,
        Home, CircleQuestion} from 'grommet-icons';

class TopActionBar extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            showHover : false
        };
    }

    render(){

        return(<Box
            // justify all its content with the main axis
            justify = "center"

            // defines the direction of the central axis
            direction = "row"

            // make it appear on top of its background
            elevation = "small"

            // aligns itself (center) with the immediate parent's central axis
            alignSelf = "center"

            // dimension with respect to its parent component
            width = "35%"
            height = "9%"

            // some gap to breathe
            margin = {{
                "top" : "1%"
            }}

            // background to make it stand out
            background = {{
                "color": "brand",
                "opacity": "strong"
            }}

            // border design
            border = {{
                "color": "border",
                "size": "medium",
                "style": "groove",
                "side": "all"
            }}

            // set the edges to round
            round = "medium"
            >
            <Box
                align = "center"
                as = "header"
                direction = "row"
                flex = "false"
                gap = "medium"
                justify = "between"
                overflow={{ horizontal: 'hidden' }}
            >
                <Button icon={<Home />} ></Button>
                <Button icon={<CaretUp />}></Button>
                <Button icon={<CaretDown />}></Button>
                <Button icon={<CaretPrevious />}></Button>
                <Button icon={<CaretNext />}></Button>
                <Box>
                    <Button icon={<CircleQuestion />}></Button>
                    { this.showHelp &&
                        <Layer
                        onEsc={() => this.removeHelp(false)}
                        onClickOutside={() => this.removeHelp(false)}
                        position = "top-right"
                        >   
                        </Layer>
                    }
                </Box>
              </Box>            
        </Box>)
    }

    showHelp(){
        this.setState({
            showHelp : true
        });
    }

    removeHelp(){
        this.setState({
            showHelp : false
        });        
    }

}

export default TopActionBar;
