import React from 'react';
import { Box, List, Layer } from 'grommet';
import { Notification } from 'grommet-icons';

class InfoPanel extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
        };
    }

    render(){
        return(
        <Layer
            animate={false}
            animation={false}
            full={false}>
        <Box
            // justify all its content with the main axis
            justify = "center"

            // defines the direction of the central axis
            direction = "row"

            // make it appear on top of its background
            elevation = "small"

            // aligns itself (end - right side) with the immediate parent's central axis
            alignSelf = "end"

            // dimension with respect to its parent component
            width = "15%"
            height = "50%"

            // border definition
            border={{
                "color": "border",
                "size": "medium",
                "style": "dashed",
                "side": "all"
              }}>

            <Box
                direction="column"
                justify="center"
            >
                <List
                primaryKey="name"
                secondaryKey="percent"
                data={[
                    { name: 'Icon', percent: 'Text' },
                    { name: 'Icon', percent: 'Text' },
                    { name: 'Icon', percent: 'Text' },
                    { name: 'Icon', percent: 'Text' },
                ]}>
                </List>
              </Box>
        </Box>
        </Layer>)
    }
}

export default InfoPanel;
