import React from 'react';
import { Box, List, Stack } from 'grommet';
import { Notification } from 'grommet-icons';

class InfoPanel extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
        };
    }

    render(){
        return(
        <Box
            // justify all its content with the main axis
            justify = "center"

            // defines the direction of the central axis
            direction = "row"

            // make it appear on top of its background
            elevation = "small"

            // aligns itself (end - right side) with the immediate parent's central axis
            alignSelf = "end"

            // dimension with respect to its parent component
            width = "15%"
            height = "50%"

            // border definition to eccentuate this component
            border={{
                "color": "border",
                "size": "medium",
                "style": "groove",
                "side": "all"
              }}

            // background to make it stand out
            background = {{
                "color": "brand",
                "opacity": "strong"
            }}

            // consistent rounded edges
            round = "medium"
            >

            <Box
                direction="column"
                justify="center"
            >
                <Button icon={<Home />}></Button>
                <Button icon={<CaretUp />}></Button>
                <Button icon={<CaretDown />}></Button>
                <Button icon={<CaretPrevious />}></Button>
                <Button icon={<CaretNext />}></Button>
            </Box>
        </Box>)
    }
}

export default InfoPanel;
