import React from 'react';
import { Box, List } from 'grommet';
import { Notification } from 'grommet-icons';

class TopActionBar extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
        };
    }

    render(){
        return(<Box
            // justify all its content with the main axis
            justify = "center"

            // defines the direction of the central axis
            direction = "row"

            // make it appear on top of its background
            elevation = "small"

            // aligns itself (center) with the immediate parent's central axis
            alignSelf = "center"

            // dimension with respect to its parent component
            width = "60%"
            height = "10%"

            // border definition
            border={{
                "color": "border",
                "size": "medium",
                "style": "dashed",
                "side": "all"
              }}
            >
            <Box
                direction="row"
                justify="center"
            >
                <List
                direction="row"
                justify="center"
                primaryKey="name"
                secondaryKey="percent"
                data={[
                    { name: 'Icon', percent: 'Text' },
                    { name: 'Icon', percent: 'Text' },
                    { name: 'Icon', percent: 'Text' },
                    { name: 'Icon', percent: 'Text' },
                ]}>
                </List>
              </Box>            
        </Box>)
    }
}

export default TopActionBar;
