// import react for JSX 
import React from 'react';

// import grommet to use their built-in component
import { Box, Stack, Image } from 'grommet';

// import Gifplyaer 
import GifPlayer from 'react-gif-player'

// import react delay
import Delay from 'react-delay'

// import sub components
import InfoPanel from './toursubcomponents/InfoPanel'
import TopActionBar from './toursubcomponents/TopActionBar'

// import image resources
import pic1 from './a.png'
import pic2 from './b.jpg'

// import gif 
import gif from './AtoB.gif'

class Tour extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
            moveUp : false,
            moveDown : false,
            moveLeft : false,
            moveRight : false,
            renderGif : true
        };

        // binding all these methods to the child components to access their state update
        this.getActionUpdate = this.getActionUpdate.bind(this)
        this.gifRendering = this.gifRendering.bind(this)
    }

    render(){
        let component = 
        <Box
        width = "96%"
        alignSelf = "center"
        margin = {{
            left : "2%",
            right : "2%"
        }}
        border={{ color: 'brand', size: 'small' }}
        >
        <Stack>
            <Box
                width = "100%"
                height = "600px"
                round = "large"
                >
            {this.gifRendering()}
            </Box>
        <Box
            wrap = {false}
            width = "100%"
            height = "670px"
            >
            {/* Passing down the parent's getActionUpdate as a prop to be called in the
            child component, it then changes the state in the parent component */}
            <TopActionBar getActionUpdate={this.getUpActionUpdate, this.getDownActionUpdate, 
                    this.getLeftActionUpdate, this.getRightActionUpdate}/>
            <InfoPanel />
        </Box>
        </Stack>
        </Box>
        return(component)
    }

    getActionUpdate(direction) {
        this.setState( function() {
            if (direction==="up"){
                return {
                    moveUp : !this.state.moveUp,
                    renderGif : !this.state.renderGif
                }
            }else if(direction==="down"){
                return {
                    moveDown : !this.state.moveDown,
                    renderGif : !this.state.renderGif
                }
            }else if(direction==="right"){
                return {
                    no : !this.state.moveRight,
                    renderGif : !this.state.renderGif
                }
            }else{
                return {
                    moveUp : !this.state.moveLeft,
                    renderGif : !this.state.renderGif
                }
            }
            return;
        })
    }

    gifRendering() {

        let displayPic = pic1;

        if(this.state.moveUp){
            displayPic = pic1;
        }else{
            displayPic = pic2;
        }

        if(this.state.renderGif){
            return(
                <Box
                    direction="row"
                    justify="center"
                >
                    {/* <GifPlayer
                        gif={gif} // load gif  
                        autoplay={true} // enable auto play
                    /> */}
                    <Image     
                        fit="cover"
                        fill={true}
                        src={gif}
                        />
                </Box>
            )
        }
        else{
            return(
                <Image     
                fit="cover"
                fill={true}
                src={displayPic}
                />
            )
        }
    }
}

export default Tour;
