import React from 'react';
import { Box, Button } from 'grommet';
import { Home } from 'grommet-icons';

class InfoPanel extends React.Component{
    constructor(props) {
        super(props);

        this.state = {
        };
    }

    render(){
        return(
        <Box
            // justify all its content with the main axis
            justify = "center"

            // defines the direction of the central axis
            direction = "row"

            // make it appear on top of its background
            elevation = "small"

            // aligns itself (end - right side) with the immediate parent's central axis
            alignSelf = "end"

            // dimension with respect to its parent component
            width = "15%"
            height = "40%"

            // giving the panel some space to breathe
            margin = {{
                top:"8%",
                bottom:"2%"
            }}
            // border definition to eccentuate this component
            border={{
                "color": "border",
                "size": "medium",
                "style": "groove",
                "side": "all"
              }}

            // background to make it stand out
            background = {{
                "color": "brand",
                "opacity": "strong"
            }}

            // consistent rounded edges
            round = "medium"
            >

            <Box
                direction="column"
                justify="center"
            >
                <Button
                icon={<Home />}
                label="Juice about this place"
                margin = "small"
                onClick={() => {}}
                />
                <Button
                icon={<Home />}
                label="See more nice shots"
                margin = "small"
                onClick={() => {}}
                />
            </Box>
        </Box>)
    }
}

export default InfoPanel;
