import React, { useState } from 'react';
import { Grommet, Box } from 'grommet';

import Footer from './components/Footer'
import Introduction from './components/Introduction'
import Tour from './components/Tour'


// defines a theme as the global style to use for the entire page
const theme = {
  global: {
    colors: {
      brand: 'white',
      },
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

function App() {
  console.log(theme)

  return (
    // potentially bad practice by not utilising theme variable to set overall background
    <Box 
      background="linear-gradient(102.77deg, #865ED6 -9.18%, #00004B 209.09%)"
    >
      <Grommet theme={theme} full>
          <Introduction />
          <Tour />
          <Footer />
      </Grommet>
    </Box>
  );
}

export default App;
